import { useGetAllNamesQuery } from "../../redux/rickMortyApi"
import { setFilter } from "../../redux/rickMortySlice"
import { useForm } from "react-hook-form"
import { useDispatch } from "react-redux"

export const FilterForm = ({ filter }) => {

  const { data, isSuccess } = useGetAllNamesQuery()

  const dispatch = useDispatch()

  const clearDuplicates = (array) => {
    let sorted = new Set(array)
    const sortedArr = []
    for (let value of sorted) sortedArr.push(value)
    return sortedArr
  }

  const {
    register, 
    handleSubmit,
    reset
  } = useForm()

  const onSubmit = (data) => {
    // console.log(data[filter], filter)
    dispatch(setFilter({ filter, name: data[filter] }))
    reset()
  }


  return (
    <form className="input-group input-group-sm my-4" onSubmit={handleSubmit(onSubmit)}>
      <select className="form-control" {...register(filter)} >
        <option label={`select ${species}`}></option>
        {
          isSuccess ?
          clearDuplicates(data.map(item => item[filter]))
            .map((item, i) => 
              <option key={i} label={item}>{item}</option>
            ) :
            null
        }
      </select>
      <button
        className="btn" 
        type='submit'
      >
        {filter[0].toUpperCase() + filter.slice(1)}
      </button>
    </form>
  )
}
