import { getAllEpisodesAsync, getEpisodesAsync } from "../redux/rickMortySlice"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { PaginationFilter } from "./reusable/PaginationFilter"

export const Episodes = () => {

  const filterparams = ['name', 'episode']
  const title = 'Browse through episodes'

  const {
     displayEpisodes,
     displayAllEpisodes,
     allEpisodesStatus,
     allEpisodesError, 
     filters, 
     page 
  } = useSelector(state => state.rickMorty)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getAllEpisodesAsync())
  }, [dispatch])

  useEffect(() => {
    dispatch(getEpisodesAsync({
      query: 'episode', 
      page, 
      filter: filters.episodeFilter.filter,
      name: filters.episodeFilter.name
    }))
  },[dispatch, page, filters.episodeFilter.filter, filters.episodeFilter.name])

  return (
    <div className="container-fluid mt-5 custom-width">
    {console.log(displayEpisodes)}
      <PaginationFilter 
        filterparams={filterparams}
        filter={filters.episodeFilter.filter}
        display={displayAllEpisodes}
        status={allEpisodesStatus}
        error={allEpisodesError}
        asyncFunc={getAllEpisodesAsync}
        name={filters.episodeFilter.name}
        title={title}
      />
      <table className="table table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">First</th>
          <th scope="col">Last</th>
          <th scope="col">Handle</th>
        </tr>
      </thead>
      </table>
    </div>
  )
}
