import { Character } from "./Character"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { useForm } from "react-hook-form"
import { useGetAllNamesQuery } from "../redux/rickMortyApi"
import { flipPage, getSpeciesAsync, setFilter } from "../redux/rickMortySlice"

export function CharacterList () {

  const { data, isSuccess } = useGetAllNamesQuery()

  const clearDuplicates = (array) => {
    let sorted = new Set(array)
    const sortedArr = []
    for (let value of sorted) sortedArr.push(value)
    return sortedArr
  }

  const {
    page, 
    filter,
    name,
    displaySpecies, 
    filteredFetchingStatus, 
    error
  } = useSelector((state) => state.rickMorty)
  const dispatch = useDispatch()

  const {
    register: speciesReg, 
    handleSubmit: speciesSubmit,
    reset: speciesReset
  } = useForm()

  const {
    register: statusReg, 
    handleSubmit: statusSubmit
  } = useForm()

  const {
    register: genderReg, 
    handleSubmit: genderSubmit
  } = useForm()

  useEffect(() => {
    dispatch(getSpeciesAsync([page, filter, name]))
  }, [dispatch, page, filter, name])

  const handleSubmit = (filter, resetType) => {
    return function (data) {
      // console.log(data[filter], filter)
      dispatch(setFilter({ filter, name: data[filter] }))
      resetType()
    }
  }


  return (
    <div className='container-fluid mt-5 custom-width'>
      {isSuccess ? console.log(data) : null}
      <h3 className='h3 text-center my-4'>Browse List of Characters</h3>
      <p className="my-3 text-center">Flip through the pages... each page displays 20 characters.</p>
      <p className="text-center">Filter by:</p>
      {
        filter !== '' ?
        <p className="text-center">{filter[0].toUpperCase() + filter.slice(1)} - "{name}"</p> :
        <p className="text-center">(Choose filter parameters)</p>
      }
      <div className="d-flex justify-content-center">
        <form className="input-group input-group-sm my-4" onSubmit={speciesSubmit(handleSubmit('species', speciesReset))}>
          <select className="form-control" {...speciesReg('species')} >
            <option label='select species'></option>
            {
              isSuccess ?
              clearDuplicates(data.map(item => item.species))
               .map((item, i) => 
                 <option key={i} label={item}>{item}</option>
               ) :
               null
            }
          </select>
          <button
            className="btn" 
            type='submit'
          >
              Species
            </button>
        </form>
        <form className="input-group input-group-sm my-4">
          <select className="form-control" {...statusReg('status')} >
            <option label='select status'></option>
            {
              isSuccess ?
              clearDuplicates(data.map(item => item.species))
               .map((item, i) => 
                 <option key={i} label={item}>{item}</option>
               ) :
               null
            }
          </select>
          {/* <input 
            {...statusReg('status')} 
            className="form-control"  
            type='text' 
            placeholder="enter status" 
          /> */}
          <button className="btn" >Status</button>
        </form>
        <form className="input-group input-group-sm my-4">
          <input 
            {...genderReg('gender')} 
            className="form-control"  
            type='text' 
            placeholder="enter gender" 
          />
          <button className="btn" >Gender</button>
        </form>
      </div>
      <div className="d-flex justify-content-between input-group input-group-sm my-4">
        <button className={page === 1 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(-1))}>Prev: {page === 1 ? '' : page - 1}</button>
        <span className="sm my-auto mx-auto">Page {page}</span>
        <button className={page === 42 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(+1))}>Next: {page === 42 ? '' : page + 1}</button>
      </div>
      { 
        filteredFetchingStatus === 'loading' ? 
        <p>Loading...</p> :
        filteredFetchingStatus === 'succeeded' ?
        displaySpecies.results.map(char => 
          <Character 
            key={char.id + 1}
            id={char.id}
            name={char.name}
            species={char.species}
            gender={char.gender}
            location={char.location}
            status={char.status}
            episode={char.episode}
            created={char.created}
            image={char.image}
          />
        ) :
        filteredFetchingStatus === 'failed' ? 
        <p>Oops, {error.toLowerCase()}</p> :
        null
      }
    </div>
  )
}
