import { useState } from "react";
import { useGetEpisodeForCharacterQuery } from "../redux/rickMortyApi";

export const Character = ({ 
  name, 
  species, 
  gender,  
  location, 
  status,
  episode,
  created, 
  image
}) => {

  const [ clicked, setClicked ] = useState(false)

  let idsArr = episode.map(ep => Number(ep.slice(-2).replace('/', '')))
  let ids = idsArr.join(',')

  const { data: episodes, isFetching, isSuccess, isError, error } = useGetEpisodeForCharacterQuery(ids)

  const episodesName = () => {
    if (Array.isArray(episodes)) {
      return episodes.map(ep => ep.name).join(', ') 
    } else {
      return episodes.name
    }
  }

  const episodesSeries = () => {
    if (Array.isArray(episodes)) {
      return episodes.map(ep => ep.episode).join(', ') 
    } else {
      return episodes.episode
    }
  }



  const classToggle = () => {
    setClicked(!clicked)
  }

  return ( 
    <div className="card w-25 rightC">
      <img src={image} className="card-img-top" alt={name} />
      <div className="card-body col">
        <h5 className="card-title">Name: {name}, Status: {status}</h5>  

        <div className={!clicked ? "d-none" : "card-text"}>
          <p>Species: {species}</p>
          <p>Gender: {gender}</p>
          <p>Location: {location.name}</p>
          <div>Episode(s):
            <div>series: {isSuccess ? episodesSeries() : null}</div>
            <div>title: {isSuccess ? episodesName() : null}</div>
          </div>
        </div>
        <button className="btn btn-primary" onClick={() => classToggle()}>{!clicked ? 'Show Info' : 'Hide Info'}</button>
      </div>
    </div>
  )
}
