import { useForm } from 'react-hook-form';
import { useEffect, useState } from 'react';
import { getAllEpisodesAsync, setSearchFormEpisode } from '../redux/rickMortySlice';
import { useSelector, useDispatch } from 'react-redux';

export const WatchList = () => {

  const { register, handleSubmit, reset } = useForm();

  const [ savedEpisodes, setSavedEpisodes ] = useState([])
  const [ error, setError ] = useState(null)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getAllEpisodesAsync())
  }, [dispatch])


  const {
    displayAllEpisodes,
    allEpisodesStatus,
    allEpisodesError, 
    searchFormEpisode
  } = useSelector(state => state.rickMorty)

  const getEpisodeByName = async () => {
    try {
      const response = await fetch(
        `https://rickandmortyapi.com/api/episode/?name=${searchFormEpisode}`
      )
      let result = await response.json()
      setSavedEpisodes(prev => [...prev, result])
    } catch (err) {
      setError(err.message)
    }
  }

  useEffect(() => {
    getEpisodeByName()
  }, [searchFormEpisode])


  const onSubmit = (value) => {
    dispatch(setSearchFormEpisode(value.episode))
    console.log(value.episode)
    reset();
  }


  return (
    <div className='container-fluid mt-5 custom-width'>
      <h3 className='h3 text-center my-4'>My Watchlist</h3>
      {/* {console.log(displayAllEpisodes)} */}
      {/* {
        searchFormEpisode === '' ?
        <p className="my-3">Enter name...</p> :
        allEpisodesStatus === 'loading' ?
        <p className="my-3">Loading...</p> :
        speciesStatus === 'succeeded' && displaySpecies.results ?
        <p className="my-3">Look what I found:</p> :
        speciesStatus === 'succeeded' && displaySpecies.error ?
        <p className="my-3">Ops, {displaySpecies.error.toLowerCase()}!</p> :
        speciesStatus === 'rejected' && speciesError ?
        <p className="my-3">Ops, {speciesError.toLowerCase()}!</p> :
        null
      } */}

      <form className='input-group input-group-sm my-4' onSubmit={handleSubmit(onSubmit)}>
        <input 
          className="form-control" 
          list="datalistOptions" 
          id="exampleDataList" 
          {...register('episode')} 
          aria-label="Text input with dropdown button" 
          autoComplete="off" 
        />
        <datalist className='datalist' id="datalistOptions">

          { 
            allEpisodesStatus === 'loading' ?
            <option label='loading'>Loading...</option> :
            allEpisodesStatus === 'succeeded' && displayAllEpisodes ?
            displayAllEpisodes.map(ep => 
              <option key={ep.id} value={ep.name} />
            ) :
            allEpisodesStatus === 'failed' && allEpisodesError ?
            <option label='error'>Oops, {allEpisodesError}</option> :
            null
          }

        </datalist>
        <button className="btn" type="button" onClick={handleSubmit(onSubmit)}>Start/Reset</button>
      </form>
    </div>
  )
}
