import { setWatchList } from "../../redux/rickMortySlice"
import { useSelector, useDispatch } from 'react-redux'

export const Table = ({
  col1,
  col2,
  col3,
  col4,
  display,
  status,
  error,
  mapProps,
  dataArr, 
  onClick
}) => {

  const dispatch = useDispatch()

  const { watchlist } = useSelector(state => state.rickMorty)

  const removeHandler = (e) => {
    localStorage.removeItem(e.currentTarget.id)
    if (localStorage.length) {
      let arr = []
      for (let i = 0; i < localStorage.length; i++) {
        let obj = JSON.parse(localStorage.getItem(localStorage.key(i)))
        arr.push(obj)
      }
      dispatch(setWatchList(arr))
    } else {
      dispatch(setWatchList(arr))
    }
  }



  const displayedWithProps =
    <>
      {
        status === 'loading' ?
        <tr>
          <th scope="row">#</th>
          <td>Loading...</td>
          <td>Loading...</td>
          <td>Loading...</td>
        </tr> :
        status === 'succeeded' && display.results ?
        display.results.map((item, i) => 
        <tr onClick={onClick} className='clicker' key={item.id} >
          <th scope="row">{i + 1}</th>
          <td>{item[mapProps.one]}</td>
          <td>{item[mapProps.two]}</td>
          <td>{item[mapProps.three]}</td>
          {
            mapProps.four ? 
            <td>{item[mapProps.four]}</td> :
            null
          }
        </tr> 
        ) :
        status === 'succeeded' && display.error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{display.error.toLowerCase()}</td>
        </tr> :
        status === 'failed' && error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{error}</td>
        </tr> :
        null
      }
    </>

  const displayedWithoutProps = 
    <>
      { 
        dataArr ?
        dataArr.map((item, i) => 
          <tr onClick={onClick} className='clicker' key={item.id} id={item.id}>
            <th scope="row">{i + 1}</th>
            <td>{item[mapProps.one]}</td>
            <td>{item[mapProps.two]}</td>
            <td>{item[mapProps.three]}</td>
            {
              mapProps.four ? 
              <td>{mapProps.four}</td> :
              null
            }
          </tr>  
        ) :
        null
      }
    </>





  return (
    <>
    <table className="table table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">{col1}</th>
          <th scope="col">{col2}</th>
          <th scope="col">{col3}</th>
          {
            col4 ?
            <th scope="col">{col4}</th> :
            null
          }
        </tr>
      </thead>
      <tbody>
      {
        dataArr === undefined ?
        displayedWithProps :
        displayedWithoutProps
        // dataArr ?
        // displayedWithoutProps :
        // null
      }
      {/* { 
        status && display && error ?
        {displayedProp} :
        dataArr ?
        dataArr.map((item, i) => 
          <tr key={item.id}>
            <th scope="row">{i + 1}</th>
            <td>{item[mapProps.one]}</td>
            <td>{item[mapProps.two]}</td>
            <td>{item[mapProps.three]}</td>
            {
              mapProps.four ? 
              <td>{item[mapProps.four]}</td> :
              null
            }
          </tr>  
        ) :
        null
      } */}
      </tbody>
    </table>
    </>
  )
}
