import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const getSpeciesAsync = createAsyncThunk(
  'displaySpecies/fetchSpecies', 
  async (payload) => {
    const response = await fetch(
      `https://rickandmortyapi.com/api/character/?page=${payload.page}&${payload.filter}=${payload.name}`
    )
    let result = response.json()
    return result
  }
)

export const getAllAsync = createAsyncThunk(
  'displayAll/fetchAll',
  async (payload) => {
    const pages = new Array(payload.count)
      .fill()
      .map((p, i) => p = i + 1)
    const results = await Promise.all(
      pages.map(page => 
        fetch(`https://rickandmoryapi.com/api/${payload.query}?page=${page}`)
          .then(data => data.json())
      )
    )
    let mapped = results.map(res => res.results).flat()
    return { result: mapped, query: payload.query }
  } 
)



const rickMortySlice = createSlice({
  name: 'rickMorty',
  initialState: {
    page: 1,
    filter: '',
    name: '',
    users: [],
    displaySpecies: null,
    displayAllEpisodes: null,
    displayAllCharacters: null,
    speciesStatus: 'idle',
    allEpisodesStatus: 'idle',
    allCharactersStatus: 'idle',
    speciesError: null,
    allEpisodesError: null,
    allCharactersError: null
  },
  reducers: {
    setFilter: (state, { payload }) => {
      state.filter = payload.filter
      state.name = payload.name
    },
    flipPage: (state, action) => {
      state.page = state.page += action.payload
    },
    addUser: (state, { payload }) => {
      const user = {
        isLogged: payload.isLogged,
        name: payload.userName,
        userID: payload.userID,
        userEmail: payload.userEmail,
        userPicture: payload.userPicture,
        likedCharacters: []
      }
      state.users.push(user)
    },
    likedChar: (state, { payload }) => {
      const char = { 
        id: payload.id,
        name: payload.name, 
        image: payload.image,
        species: payload.species,
        gender: payload.gender,
        location: payload.location, 
        status: payload.status,
        episode: payload.episode,
        created: payload.created,  
        episodes: payload.episodes,
        title: payload.title,
        series: payload.series
      }
      let user = state.users.find(usr => usr.isLogged === true)
      let eqID = user.likedCharacters.find(chr => chr.id === payload.id)
      if (!eqID) {
        user.likedCharacters.push(char)
      }

    },
    disLikedChar: (state, {payload}) => {
      let user = state.users.find(usr => usr.isLogged === true)
      user.likedCharacters = user.likedCharacters.filter(char => char.id !== payload.charID)
    },
    logOut: (state, action) => {
      let user = state.users.find(usr => usr.isLogged === true)
      user.isLogged = false
    }
  },
  extraReducers(builder) {
    builder
      // getSpeciesAsync cases
      .addCase(getSpeciesAsync.pending, (state, action) => {
        state.speciesStatus = 'loading'
      })
      .addCase(getSpeciesAsync.fulfilled, (state, action) => {
        state.speciesStatus = 'succeeded'
        state.displaySpecies = action.payload
      })
      .addCase(getSpeciesAsync.rejected, (state, action) => {
        state.speciesStatus = 'failed'
        state.speciesError = action.error.message
      })
      // getAll cases
      .addCase(getAllAsync.fulfilled, (state, { payload }) => {
        if (payload.query === 'episode') {
          state.allEpisodesStatus = 'succeeded'
          state.displayAllEpisodes = payload.result
        } else if (payload.query === 'character') {
          state.allCharactersStatus = 'succeeded'
          state.displayAllCharacters = payload.result
        }
      })
      .addCase(getAllAsync.rejected, (state, action) => {
        if (action.payload.query === 'episode') {
          state.allEpisodesStatus = 'failed'
          state.allEpisodesError = action.error.message
        } else if (action.payload.query === 'character') {
          state.allCharactersStatus = 'failed'
          state.allCharactersError = action.error.message
        }
      })
  }
})

export const { 
  flipPage, 
  addUser, 
  likedChar, 
  disLikedChar, 
  filterChar,
  setFilter
} = rickMortySlice.actions;


export default rickMortySlice.reducer
