import { getAllLocationsAsync, getLocationsAsync, setLocationFilter } from "../redux/rickMortySlice"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { PaginationFilter } from "./reusable/PaginationFilter"
import { Table } from './reusable/Table'

export const Locations = () => {

  const filterparams = ['name', 'type', 'dimension']
  const title = 'Browse locations'
  const keyword = 'locations'

  const {
    displayLocations,
    displayAllLocations,
    locationsStatus,
    allLocationsStatus,
    locationsError,
    allLocationsError, 
    filters, 
    locationsPage 
  } = useSelector(state => state.rickMorty)

  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getLocationsAsync({
      query: 'location', 
      page: locationsPage, 
      filter: filters.locationFilter.filter,
      name: filters.locationFilter.name
    }))
  },[dispatch, locationsPage, filters.locationFilter.filter, filters.locationFilter.name])

  return (
    <div className="container-fluid mt-5 custom-width">
      {console.log(displayLocations)}
      <PaginationFilter 
        filterparams={filterparams}
        filter={filters.locationFilter.filter}
        display={displayAllLocations}
        status={allLocationsStatus}
        error={allLocationsError}
        asyncFuncAll={getAllLocationsAsync}
        asyncFuncLocal={setLocationFilter}
        name={filters.locationFilter.name}
        title={title}
        keyword={keyword}
        pageQuantity={
          locationsStatus === 'succeeded' && displayLocations.info ? 
          displayLocations.info.pages : 
          null
        }
        page={locationsPage}
      />
      {/* <table className="table table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Type</th>
          <th scope="col">Dimension</th>
        </tr>
      </thead>
      <tbody>
      {
        locationsStatus === 'loading' ?
        <tr>
          <th scope="row">#</th>
          <td>Loading...</td>
          <td>Loading...</td>
          <td>Loading...</td>
        </tr> :
        locationsStatus === 'succeeded' && displayLocations.results ?
        displayLocations.results.map((loc, i) => 
        <tr key={loc.id}>
          <th scope="row">{i + 1}</th>
          <td>{loc.name}</td>
          <td>{loc.type}</td>
          <td>{loc.dimension}</td>
        </tr> 
        ) :
        locationsStatus === 'succeeded' && displayLocations.error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{displayLocations.error.toLowerCase()}</td>
        </tr> :
        locationsStatus === 'failed' && locationsError ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{locationsError}</td>
        </tr> :
        null
      }
      </tbody>
      </table> */}
      <Table 
        col1={'Name'}
        col2={'Type'}
        col3={'Dimension'}
        display={displayLocations}
        status={locationsStatus}
        error={locationsError}
        mapProps={{
          one: 'name',
          two: 'type',
          three: 'dimension'
        }}
      />
    </div>
  )
}
