export const Table = ({
  col1,
  col2,
  col3,
  col4 = 'Hi',
  display,
  status,
  error,
  mapProps
}) => {

  // const ConditionalTable = () => {
  //   if (mapProps.four) {
  //     return (
  //       <>
  //         <td>{item[mapProps.four]}</td>
  //       </>
  //     )
  //   } else {
  //     return (
  //       null
  //     )
  //   }
  // }

  return (
    <>
    <table className="table table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">{col1}</th>
          <th scope="col">{col2}</th>
          <th scope="col">{col3}</th>
          {
            col4 ?
            <th scope="col">{col4}</th> :
            null
          }
        </tr>
      </thead>
      <tbody>
      {
        status === 'loading' ?
        <tr>
          <th scope="row">#</th>
          <td>Loading...</td>
          <td>Loading...</td>
          <td>Loading...</td>
        </tr> :
        status === 'succeeded' && display.results ?
        display.results.map((item, i) => 
        <tr key={item.id}>
          <th scope="row">{i + 1}</th>
          <td>{item[mapProps.one]}</td>
          <td>{item[mapProps.two]}</td>
          <td>{item[mapProps.three]}</td>
          {
            mapProps.four ? 
            <td>{item[mapProps.four]}</td> :
            null
          }
        </tr> 
        ) :
        status === 'succeeded' && display.error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{display.error.toLowerCase()}</td>
        </tr> :
        status === 'failed' && error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{error}</td>
        </tr> :
        null
      }
      </tbody>
    </table>
    </>
  )
}
