import { Character } from "./Character"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { FilterForm } from "./FilterForm"
import { flipPage, getSpeciesAsync, setDisplaySpecies, setError } from "../../redux/rickMortySlice"

export function CharacterList () {

  const filters = ['species', 'status', 'gender']

  const {
    page, 
    filter,
    name,
    displaySpecies, 
    filteredFetchingStatus, 
    error
  } = useSelector((state) => state.rickMorty)

  const dispatch = useDispatch()

  // const fetchQueries = async () => {
  //   const response = await fetch(
  //     `https://rickandmortyapi.com/api/character/?page=${page}&${filter}=${name}`
  //   )
  //     .then(res => res.json())
  //     .then(parsed => dispatch(setDisplaySpecies(parsed)))
  //     .catch(err => dispatch(setError(err)))

  // }

  useEffect(() => {
    dispatch(getSpeciesAsync({page, filter, name}))
    // fetchQueries()
  }, [dispatch, page, filter, name])


  return (
    <div className='container-fluid mt-5 custom-width'>
      {/* {console.log(filteredFetchingStatus)}
      {console.log(displaySpecies)}
      {console.log(error)} */}
      <h3 className='h3 text-center my-4'>Browse List of Characters</h3>
      <p className="my-3 text-center">Flip through the pages... each page displays 20 characters.</p>
      <p className="text-center">Filter by:</p>
      {
        filter !== '' ?
        <p className="text-center">{filter[0].toUpperCase() + filter.slice(1)} - "{name}"</p> :
        <p className="text-center">(Choose filter parameters)</p>
      }
      <div className="d-flex justify-content-center">
      {
        filters.map((item, i) => 
          <FilterForm 
            key={i}
            filter={item}
          />
        )
      } 
      </div>
      <div className="d-flex justify-content-between input-group input-group-sm my-4">
        <button className={page === 1 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(-1))}>Prev: {page === 1 ? '' : page - 1}</button>
        <span className="sm my-auto mx-auto">Page {page}</span>
        <button className={page === 42 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(+1))}>Next: {page === 42 ? '' : page + 1}</button>
      </div>
      { 
        filteredFetchingStatus === 'loading' ? 
        <p>Loading...</p> :
        filteredFetchingStatus === 'succeeded' && displaySpecies.results ?
        displaySpecies.results.map(char => 
          <Character 
            key={char.id + 1}
            id={char.id}
            name={char.name}
            species={char.species}
            gender={char.gender}
            location={char.location}
            status={char.status}
            episode={char.episode}
            created={char.created}
            image={char.image}
          />
        ) :
        filteredFetchingStatus === 'succeeded' && displaySpecies.error ? 
        <p>Oops, {displaySpecies.error.toLowerCase()}</p> :
        filteredFetchingStatus === 'failed' && error ?
        <p>Oops, {error}</p> :
        null
      }
    </div>
  )
}
