import { useForm } from 'react-hook-form';
import { useGetCharacterByNameQuery, useGetAllCharactersQuery, useGetAllCharactersByPageQuery } from '../redux/rickMortyApi';
import { useEffect, useState } from 'react';
import { Character } from './Character';

export function Form () {

  const { register, handleSubmit, reset } = useForm();
  const [ name, setName ] = useState('')
  // const [ pages, setPages ] = useState(0)
  const [ page, setPage ] = useState(1);
  const [ names, setNames ] = useState([]);


  const { data: character, isFetching, isSuccess, isError, error } = useGetCharacterByNameQuery(name);
  const { data: characterList, isFetching: characterListFetch, isSuccess: characterListSuccess, isLoading: characterListLoading } = useGetAllCharactersByPageQuery(page)

  const onSubmit = (value) => {
    setName(value.name);
    reset();
  }

  // const [ names, setNames ] = useState([])

  // useEffect(() => {
  //   for (let i = 0; i < 42; i++) {
  //     setPage(prev => prev + 1)
  //     // setNames(prevNames => [...prevNames, characterList.results.map(char => char.name)])
  //   }
  // }, [characterList])

  // useEffect(() => {
  //   if (characterListSuccess) {
  //     setNames(prevNames => [...prevNames, characterList.results.map(char => char.name)])
  //   }
  // }, [page])

  // const getNamesArr = () => {
  //   setNames(prevNames => [...prevNames, characterList.results.map(char => char.name)])
  // }

  const getAllNames = () => {
    const namesArr = []
      for (let i = 0; i < 42; i ++) {
        setPage(i)
        namesArr.push(characterList.results.map(char => char.name))
      }
    return setNames(prev => [...prev, namesArr])
  }


  return (
    <div className='container w-50'>
      <h3 className='h3 text-center my-4'>Find your character</h3>
      {
        name === '' ? 
        <p className="my-3">Enter name...</p> :
        isFetching ? 
        <p className="my-3">Loading...</p> :
        isSuccess ? 
        <p className="my-3">Look what I found:</p> :
        isError ?
        <p className="my-3">Ops, {error.data.error.toLowerCase()}!</p> :
        null
      }
      { 

        characterListSuccess ? console.log(page) : null
      }

      <form className='input-group input-group-sm my-4' onSubmit={handleSubmit(onSubmit)}>
        <input className="form-control" list="datalistOptions" id="exampleDataList" {...register('name')} aria-label="Text input with dropdown button" autoComplete="off" />
        <datalist id="datalistOptions">

          <option value="Oleg" />
          <option value="Ruran" />
          <option value="Ravshan" />
        </datalist>
        <button className="btn" type="button" onClick={handleSubmit(onSubmit)}>Start/Reset</button>
      </form>
      <div>
        { 
          name === '' ? 
          null :
          isSuccess ? 
          character.results.map(item => 
            <Character 
              key={item.id}
              name={item.name}
              status={item.status}
              species={item.species}
              gender={item.gender}
              location={item.location}
              episode={item.episode}
              created={item.created}
              image={item.image}
            />

          ) :
          null
        }
      </div>
    </div>
  );
}
