import { getAllEpisodesAsync, getEpisodesAsync, setEpisodeFilter } from "../redux/rickMortySlice"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { PaginationFilter } from "./reusable/PaginationFilter"

export const Episodes = () => {

  const filterparams = ['name', 'episode']
  const title = 'Browse episodes'
  const keyword = 'episodes'

  const {
     displayEpisodes,
     displayAllEpisodes,
     episodesStatus,
     allEpisodesStatus,
     episodesError,
     allEpisodesError, 
     filters, 
     episodePage 
  } = useSelector(state => state.rickMorty)

  const dispatch = useDispatch()

  // useEffect(() => {
  //   dispatch(getAllEpisodesAsync())
  // }, [dispatch])

  useEffect(() => {
    dispatch(getEpisodesAsync({
      query: 'episode', 
      page: episodePage, 
      filter: filters.episodeFilter.filter,
      name: filters.episodeFilter.name
    }))
  },[dispatch, episodePage, filters.episodeFilter.filter, filters.episodeFilter.name])

  return (
    <div className="container-fluid mt-5 custom-width">
    {console.log(displayEpisodes)}
      <PaginationFilter 
        filterparams={filterparams}
        filter={filters.episodeFilter.filter}
        display={displayAllEpisodes}
        status={allEpisodesStatus}
        error={allEpisodesError}
        asyncFuncAll={getAllEpisodesAsync}
        asyncFuncLocal={setEpisodeFilter}
        name={filters.episodeFilter.name}
        title={title}
        keyword={keyword}
        pageQuantity={
          episodesStatus === 'succeeded' && displayEpisodes.info ? 
          displayEpisodes.info.pages : 
          null
        }
        page={episodePage}
      />
      <table className="table table-hover">
      <thead>
        <tr>
          <th scope="col">#</th>
          <th scope="col">Name</th>
          <th scope="col">Code</th>
          <th scope="col">Air Date</th>
        </tr>
      </thead>
      <tbody>
      {
        episodesStatus === 'loading' ?
        <tr>
          <th scope="row">#</th>
          <td>Loading...</td>
          <td>Loading...</td>
          <td>Loading...</td>
        </tr> :
        episodesStatus === 'succeeded' && displayEpisodes.results ?
        displayEpisodes.results.map((ep, i) => 
        <tr key={ep.id}>
          <th scope="row">{i + 1}</th>
          <td>{ep.name}</td>
          <td>{ep.episode}</td>
          <td>{ep.air_date}</td>
        </tr> 
        ) :
        episodesStatus === 'succeeded' && displayEpisodes.error ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{displayEpisodes.error.toLowerCase()}</td>
        </tr> :
        episodesStatus === 'failed' && episodesError ?
        <tr>
          <th scope="row">#</th>
          <td>Oh no!</td>
          <td>looks like</td>
          <td>{episodesError}</td>
        </tr> :
        null
      }
      </tbody>
      </table>
    </div>
  )
}
