import { useState } from "react";
import { useGetEpisodeForCharacterQuery } from "../redux/rickMortyApi";

export const Character = ({ 
  name, 
  species, 
  gender,  
  location,
  episode, 
  status,
  created
}) => {

  const [ clicked, setClicked ] = useState(false)

  let idsArr = episode.map(ep => Number(ep.slice(-2).replace('/', '')))
  let ids = idsArr.join(',')

  const { data: episodes, isFetching, isSuccess, isError, error } = useGetEpisodeForCharacterQuery(ids)


  function episodeString (value) {
    if (Array.isArray(episodes)) {
      return value = 'name' ? episodes.map(ep => ep.name).join(', ') :
             value = 'episode' ? episodes.map(ep => ep.episode).join(', ') :
             null
    } else {
      return value = 'name' ? episodes.name :
             value = 'episode' ? episodes.episode :
             null
    }
  }

  const classToggle = () => {
    setClicked(!clicked)
  }

  return ( 
    <div>
      <div>
        <p>Name: {name}, <span>Status: {status}</span></p>  
      </div>
      <div className={!clicked ? "d-none" : null}>
         <p>Species: {species}</p>
         <p>Gender: {gender}</p>
         <p>Location: {location.name}</p>
         <div>Episode(s):
           <div>series: {isSuccess ? episodeString('episode') : null}</div>
           <div>title: {isSuccess ? episodeString('name') : null}</div>
         </div>
         {isSuccess ? console.log(episodes) : null}
      </div>
      <button onClick={() => classToggle()}>{!clicked ? 'Show Info' : 'Hide Info'}</button>
    </div>
  )
}
