import { Character } from "./Character"
import { useEffect } from "react"
import { useSelector, useDispatch } from "react-redux"
import { useForm } from "react-hook-form"
import { flipPage, getSpeciesAsync } from "../redux/rickMortySlice"

export function CharacterList () {

  const {page, displaySpecies, filteredFetchingStatus, error} = useSelector((state) => state.rickMorty)
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(getSpeciesAsync([page, '']))
  }, [dispatch, page])


  return (
    <div className='container-fluid mt-5 custom-width'>
      {console.log(displaySpecies)}
      <h3 className='h3 text-center my-4'>Browse List of Characters</h3>
      <p className="my-3">Flip through the pages... each page displays 20 characters.</p>
      <p className="text-center">Filter by:</p>
      <form
        className="d-flex justify-content-center input-group input-group-sm my-4"
      >
        <button className={page === 1 ? "btn disabled" : "btn"} >Species</button>
        <input className="form-control"  type='text' placeholder="enter species" />
        <button className={page === 42 ? "btn disabled" : "btn"} >Status</button>
        <input className="form-control"  type='text' placeholder="enter status" />
        <button className={page === 42 ? "btn disabled" : "btn"} >Gender</button>
        <input className="form-control"  type='text' placeholder="enter gender" />
      </form>
      <div className="d-flex justify-content-between input-group input-group-sm my-4">
        <button className={page === 1 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(-1))}>Prev: {page === 1 ? '' : page - 1}</button>
        <span className="sm my-auto mx-auto">Page {page}</span>
        <button className={page === 42 ? "btn disabled" : "btn"} onClick={() => dispatch(flipPage(+1))}>Next: {page === 42 ? '' : page + 1}</button>
      </div>
      { 
        filteredFetchingStatus === 'loading' ? 
        <p>Loading...</p> :
        filteredFetchingStatus === 'succeeded' ?
        displaySpecies.results.map(char => 
          <Character 
            key={char.id + 1}
            id={char.id}
            name={char.name}
            species={char.species}
            gender={char.gender}
            location={char.location}
            status={char.status}
            episode={char.episode}
            created={char.created}
            image={char.image}
          />
        ) :
        filteredFetchingStatus === 'failed' ? 
        <p>Oops, {error.toLowerCase()}</p> :
        null
      }
    </div>
  )
}
