import ReactFacebookLogin from "react-facebook-login";
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addUser } from "../redux/rickMortySlice";

export const Facebook = () => {

  const dispatch = useDispatch()
  const { users } = useSelector((state) => state.rickMorty)

  // const [ isLoggedIn, setIsLoggedIn ] = useState(false)
  // const [ userID, setUserID ] = useState('')
  // const [ userName, setUserName ] = useState('')
  // const [ userEmail, setUserEmail ] = useState('')
  // const [ userPicture, setUserPicture ] = useState('')

  const [ state, setState ] = useState({
    login: false,
    name : '',
    picture: ''
  })

  // const findUser = users.find(user => user.id )

  const componentClicked = () => {
    console.log('clicked')
  }

  const responseFacebook = (response) => {
    // setIsLoggedIn(!isLoggedIn)
    // setUserID(response.userID)
    // setUserName(response.name)
    // setUserEmail(response.email)
    // setUserPicture(response.picture.data.url)

    dispatch(addUser({ 
      userName: response.name, 
      userID: response.userID, 
      userEmail: response.email, 
      userPicture: response.picture.data.url
    }))

    setState(prev => (
      {
        ...prev, 
        login: true,
        name: response.name,
        picture: response.picture.data.url
      }
    ))
  }

  let fbContent;

  if (state.login) {
    fbContent = (
      <div>
        <img src={state.picture} alt={state.name} />
        <h6>Welcome, {state.name}</h6>
      </div>
    )
  } else {
    fbContent = (
      <ReactFacebookLogin
        size='small'
        appId="590001315795132"
        autoLoad={true}
        fields="name,email,picture"
        onClick={componentClicked}
        callback={responseFacebook} 
      />
    )
  }

  return (
    <>
      {fbContent}
    </>
  )
}
